package com.lao.loppingStatements;



public class ForLoopExample {

	
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//here int value is zero which is loop initialization statement;
		//i<25 is condition;if this condition satisfies the execution takes place
		//then increment or decrement should be mentioned
		//when increment or decrement unsatisfy the condition mention...the loop breaks;
	for(int i =0;i<25;i++) {
		System.out.println("I have Executed For Loop");
		
	}

	}

}
