package com.lao.loppingStatements;

public class WhileAndDoWhileExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * int i =0; while(i<25) { System.out.println("I have Executed While Loop");
		 * i++; }
		 */
		
		
		int j =1;
		do{
			System.out.println("I have Executed Do-While Loop ");
			j++;
		}while(j<26) ;
		
	

	}
}
